INSERT INTO `task_notify_ideation_cf` (`id_task`, `sender_name`, `sender_email`, `subject`, `message`, `recipients_cc`, `recipients_bcc`, `isFollowers`, `isDepositaire`) VALUES
	(2, 'Mairie de Paris', 'no-reply@paris.fr', 'Budget participatif - Dépôt d\'une idée', '<p>Cher { $pseudo },</p>\r\n<p>&nbsp;</p>\r\n<p>Votre id&eacute;e a bien &eacute;t&eacute; prise en compte, elle apparaitra tr&egrave;s prochainement sur le site.</p>\r\n<p>&nbsp;</p>\r\n<p>Merci pour votre contribution,</p>\r\n<p>L\'&eacute;quipe du Budget Participatif</p>', '', 'ddct-mpc-site-bp@paris.fr', 0, 0),
	(4, 'Ville de Paris - budget participatif', 'budget.participatif@paris.fr', 'Projet recevable !', '<p>Bonjour,</p>\r\n<p>Le projet &laquo; ${idee.titre!\'\'} &raquo;, que vous avez d&eacute;pos&eacute; ou auquel vous &ecirc;tes associ&eacute;, entre dans une nouvelle phase.</p>\r\n<p>Les services techniques de la Ville de Paris vont pr&eacute;-&eacute;tudier sa faisabilit&eacute; et &eacute;valuer son co&ucirc;t. Cette pr&eacute;-&eacute;tude a pour but de d&eacute;tecter d&rsquo;&eacute;ventuels obstacles qui emp&ecirc;cheraient sa mise au vote.</p>\r\n<p>Nous vous prions de noter que <strong>cette pr&eacute;-&eacute;tude a pour seul objectif de d&eacute;terminer la faisabilit&eacute; du projet et son co&ucirc;t pr&eacute;visionnel, mais non d&rsquo;arr&ecirc;ter d&eacute;finitivement les d&eacute;tails de sa mise en &oelig;uvre.</strong> Ces derniers seront pr&eacute;cis&eacute;s uniquement si le projet remporte le vote de septembre.<br />Nous vous ferons part prochainement des r&eacute;sultats de cette pr&eacute;-&eacute;tude.</p>\r\n<p>Pour toute question, nous vous invitons &agrave; consulter <span style="text-decoration: underline;"><span style="color: #0000ff; text-decoration: underline;"><a style="color: #0000ff; text-decoration: underline;" href="https://budgetparticipatif.paris.fr/bp/jsp/site/Portal.jsp?page=helpdesk&amp;faq_id=3" target="_blank">la foire aux questions du budget participatif</a></span></span> ou nous &eacute;crire &agrave; <span style="text-decoration: underline;"><span style="color: #0000ff; text-decoration: underline;"><a style="color: #0000ff; text-decoration: underline;" href="mailto:budgetparticipatif@paris.fr" target="_blank">budgetparticipatif@paris.fr</a></span></span></p>\r\n<p><strong>L\'&eacute;quipe du budget participatif de la ville de Paris</strong></p>\r\n<p><img src="http://budgetparticipatif.paris.fr/bp/images/local/skin/logo_paris_bp_mini.png" alt="" /></p>\r\n<p>#budgetparticipatif</p>\r\n<p>Nous suivre sur <span style="text-decoration: underline; color: #0000ff;"><a style="color: #0000ff; text-decoration: underline;" href="https://www.facebook.com/groups/NotreBudget/" target="_blank">Facebook</a></span> et sur&nbsp;<span style="text-decoration: underline; color: #0000ff;"><a style="color: #0000ff; text-decoration: underline;" href="https://twitter.com/Paris" target="_blank">Twitter</a></span></p>\r\n<p style="font-size: 0.875em;">Pour param&eacute;trer les notifications que vous recevez, rendez-vous dans la rubrique &laquo; Mes notifications &raquo; de votre compte sur <span style="text-decoration: underline;"><span style="color: #3366ff;"><a style="color: #3366ff; text-decoration: underline;" href="http://budgetparticipatif.paris">budgetparticipatif.paris</a></span></span></p>', '', 'ddct-mpc-site-bp@paris.fr', 1, 1),
	(6, 'Ville de Paris - budget participatif', 'budget.participatif@paris.fr', 'Un projet va être co-construit', '<p>Bonjour,</p>\r\n<p>Nous vous informons que le projet &laquo; ${idee.titre!\'\'} &raquo; va faire l&rsquo;objet d&rsquo;un atelier de co-construction. <br />Cet atelier visera &agrave; construire un projet unique qui fusionnera d&rsquo;autres propositions proches de la v&ocirc;tre. Vous serez prochainement invit&eacute;&middot;e par courriel &agrave; y participer.</p>\r\n<p>Pour toute question, vous pouvez consulter <span style="text-decoration: underline;"><span style="color: #0000ff;"><a style="color: #0000ff; text-decoration: underline;" href="https://budgetparticipatif.paris.fr/bp/jsp/site/Portal.jsp?page=helpdesk&amp;faq_id=3" target="_blank">la foire aux questions du budget participatif</a></span></span> ou nous &eacute;crire &agrave; <span style="text-decoration: underline;"><span style="color: #0000ff;"><a style="color: #0000ff; text-decoration: underline;" href="mailto:budget.participatif@paris.fr" target="_blank">budget.participatif@paris.fr</a></span></span></p>\r\n<p>Bien sinc&egrave;rement,<br /><strong>L&rsquo;&eacute;quipe du budget participatif de la Ville de Paris</strong></p>\r\n<p><img src="http://budgetparticipatif.paris.fr/bp/images/local/skin/logo_paris_bp_mini.png" alt="" /></p>\r\n<p>#budgetparticipatif</p>\r\n<p>Nous suivre sur <span style="text-decoration: underline;"><span style="color: #0000ff;"><a style="color: #0000ff; text-decoration: underline;" href="https://www.facebook.com/groups/NotreBudget/" target="_blank">Facebook</a></span></span> et sur <span style="text-decoration: underline;"><span style="color: #0000ff;"><a style="color: #0000ff; text-decoration: underline;" href="https://twitter.com/Paris" target="_blank">Twitter</a></span></span></p>\r\n<p style="font-size: 0.875em;">Pour param&eacute;trer les notifications que vous recevez, rendez-vous dans la rubrique &laquo; Mes notifications &raquo; de votre compte sur <span style="text-decoration: underline;"><span style="color: #3366ff; text-decoration: underline;"><a style="color: #3366ff; text-decoration: underline;" href="http://budgetparticipatif.paris" target="_blank">budgetparticipatif.paris</a></span></span></p>', '', 'ddct-mpc-site-bp@paris.fr', 1, 1),
	(8, 'Ville de Paris - budget participatif', 'budget.participatif@paris.fr', 'À propos de votre projet', '<p>Bonjour,</p>\r\n<p>Nous vous informons que le projet &laquo; ${idee.titre!\'(titre inconnu)\'} &raquo; ne peut &ecirc;tre retenu pour la raison suivante :</p>\r\n<p>${idee.motifRecev!\'(motif non pr&eacute;cis&eacute;)\'}</p>\r\n<p>Sachez que vous avez la possibilit&eacute; de vous associer &agrave; d&rsquo;autres projets et de concourir &agrave; leur succ&egrave;s. Pour ce faire, rendez-vous sur la fiche du projet que vous souhaitez suivre et cliquez sur le bouton &laquo; je m&rsquo;associe &raquo;. Cette fonction vous permet d&rsquo;&ecirc;tre inform&eacute;&middot;e de tous les temps forts li&eacute;s &agrave; la mise en &oelig;uvre de ce projet.</p>\r\n<p>Pour toute question, vous pouvez consulter <span style="text-decoration: underline; color: #0000ff;"><a style="color: #0000ff; text-decoration: underline;" href="https://budgetparticipatif.paris.fr/bp/jsp/site/Portal.jsp?page=helpdesk&amp;faq_id=3" target="_blank">la foire aux questions du budget participatif</a> </span>ou nous &eacute;crire &agrave; <span style="text-decoration: underline;"><span style="color: #0000ff; text-decoration: underline;"><a style="color: #0000ff; text-decoration: underline;" href="mailto:budget.participatif@paris.fr" target="_blank">budget.participatif@paris.fr</a></span></span></p>\r\n<p><strong>L\'&eacute;quipe du budget participatif de la Ville de Paris</strong></p>\r\n<p><img src="http://budgetparticipatif.paris.fr/bp/images/local/skin/logo_paris_bp_mini.png" alt="" /></p>\r\n<p>#budgetparticipatif</p>\r\n<p>Nous suivre sur <span style="text-decoration: underline; color: #0000ff;"><a style="color: #0000ff; text-decoration: underline;" href="https://www.facebook.com/groups/NotreBudget/" target="_blank">Facebook</a> </span>et sur <span style="text-decoration: underline;"><span style="color: #0000ff; text-decoration: underline;"><a style="color: #0000ff; text-decoration: underline;" href="https://twitter.com/Paris" target="_blank">Twitter</a></span></span></p>\r\n<p style="font-size: 0.875em;">Pour param&eacute;trer les notifications que vous recevez, rendez-vous dans la rubrique &laquo; Mes notifications &raquo; de votre compte sur <span style="text-decoration: underline;"><span style="color: #3366ff; text-decoration: underline;"><a style="color: #3366ff; text-decoration: underline;" href="https://budgetparticipatif.paris.fr/bp/" target="_blank">budgetparticipatif.paris</a></span></span></p>', '', 'ddct-mpc-site-bp@paris.fr', 0, 1),
	(10, 'Ville de Paris - budget participatif', 'budget.participatif@paris.fr', 'Regroupement d\'un projet', '<p>Bonjour,</p>\r\n<p>La proposition &laquo; ${idee.titre!\'\'} &raquo;, que vous avez d&eacute;pos&eacute; ou &agrave; laquelle vous &ecirc;tes associ&eacute;, va &ecirc;tre regroup&eacute;e avec d&rsquo;autres projets similaires, &agrave; l&rsquo;initiative de la Ville.</p>\r\n<p>Si les projets qui le composent sont faisables techniquement, ce regroupement permettra de porter au vote un projet global et ainsi d&rsquo;augmenter ses chances de succ&egrave;s. Si ce projet global est gagnant &agrave; l&rsquo;issue du scrutin, toutes les propositions qui le composent, dont &laquo; ${idee.titre!\'\'} &raquo;, seront, sauf difficult&eacute; particuli&egrave;re, r&eacute;alis&eacute;es.</p>\r\n<p>Vous pourrez consulter le projet issu de ce regroupement en vous rendant sur votre proposition initiale dans la rubrique &laquo; Mes projets d&eacute;pos&eacute;s &raquo; accessible depuis &laquo; Mon Espace &raquo; sur le site budgetparticipatif.paris, &agrave; partir de juillet prochain. Un lien cliquable, situ&eacute; en bas de page, vous orientera vers ce projet.</p>\r\n<p>Pour toute question, vous pouvez consulter <span style="text-decoration: underline;"><span style="color: #0000ff;"><a style="color: #0000ff; text-decoration: underline;" href="https://budgetparticipatif.paris.fr/bp/jsp/site/Portal.jsp?page=helpdesk&amp;faq_id=3" target="_blank">la foire aux questions du budget participatif</a></span></span> ou nous &eacute;crire &agrave; <span style="text-decoration: underline;"><span style="color: #0000ff;"><a style="color: #0000ff; text-decoration: underline;" href="mailto:budget.participatif@paris.fr" target="_blank">budget.participatif@paris.fr</a></span></span></p>\r\n<p><strong>L\'&eacute;quipe du budget participatif de la Ville de Paris</strong></p>\r\n<p><img src="http://budgetparticipatif.paris.fr/bp/images/local/skin/logo_paris_bp_mini.png" alt="" /></p>\r\n<p>#budgetparticipatif</p>\r\n<p>Nous suivre sur <span style="text-decoration: underline;"><span style="color: #0000ff;"><a style="color: #0000ff; text-decoration: underline;" href="https://www.facebook.com/groups/NotreBudget/" target="_blank">Facebook</a></span></span> et sur <span style="text-decoration: underline; color: #0000ff;"><a style="color: #0000ff; text-decoration: underline;" href="https://twitter.com/Paris" target="_blank">Twitter</a></span></p>\r\n<p style="font-size: 0.875em;">Pour param&eacute;trer les notifications que vous recevez, rendez-vous dans la rubrique &laquo; Mes notifications &raquo; de votre compte sur <span style="text-decoration: underline;"><span style="color: #3366ff; text-decoration: underline;"><a style="color: #3366ff; text-decoration: underline;" href="https://budgetparticipatif.paris.fr/bp/">budgetparticipatif.paris</a></span></span></p>', '', 'ddct-mpc-site-bp@paris.fr', 0, 0),
	(12, 'Ville de Paris - budget participatif', 'budget.participatif@paris.fr', 'Projet retenu pour le vote !', '<p>Bonjour,</p>\r\n<p>Nous sommes heureux de vous informer que le projet &laquo; ${idee.titre!\'\'} &raquo;, sera soumis au vote !</p>\r\n<p>Le scrutin se tiendra du 7 au 23 septembre 2018 &agrave; 20H, par voie num&eacute;rique sur le site du budget participatif ou au moyen d&rsquo;urnes dispos&eacute;es dans Paris.</p>\r\n<p>Faites voter pour votre projet : parlez-en autour de vous, cr&eacute;ez des documents de campagne : t&eacute;l&eacute;chargez le kit de communication pr&eacute;vu &agrave; cet effet sur le site budgetparticipatif.paris.</p>\r\n<p>Nous vous donnons aussi rendez-vous pour une agora citoyenne o&ugrave; vous pourrez pr&eacute;senter votre projet et perfectionner votre campagne de communication. Enfin, retrouvez sur <span style="text-decoration: underline;"><span style="color: #0000ff;"><a style="color: #0000ff; text-decoration: underline;" href="https://budgetparticipatif.paris.fr/bp/jsp/site/Portal.jsp?page=agenda&amp;view=agendaEvents&amp;id=1&amp;oaq[passed]=1&amp;oaq[order]=latest" target="_blank">l&rsquo;agenda en ligne</a></span></span> tous les &eacute;v&eacute;nements li&eacute;s au budget participatif dans les arrondissements, ainsi que les emplacements des urnes de vote. Cet agenda recense &eacute;galement les ateliers d&rsquo;aide &agrave; la communication autour de votre projet, anim&eacute;s par la Ville ou des associations, et auxquels vous avez la possibilit&eacute; de participer. Saisissez-vous de ces temps forts pour promouvoir votre projet.</p>\r\n<p>Pour toute question, vous pouvez consulter <span style="text-decoration: underline;"><span style="color: #0000ff;"><a style="color: #0000ff; text-decoration: underline;" href="https://budgetparticipatif.paris.fr/bp/jsp/site/Portal.jsp?page=helpdesk&amp;faq_id=3" target="_blank">la foire aux questions du budget participatif</a></span></span> ou nous &eacute;crire &agrave; <span style="text-decoration: underline;"><span style="color: #0000ff;"><a style="color: #0000ff; text-decoration: underline;" href="mailto:budget.participatif@paris.fr" target="_blank">budget.participatif@paris.fr</a></span></span></p>\r\n<p>Bonne chance pour cette phase cruciale.</p>\r\n<p><strong>L\'&eacute;quipe du budget participatif de la Ville de Paris</strong></p>\r\n<p><img src="http://budgetparticipatif.paris.fr/bp/images/local/skin/logo_paris_bp_mini.png" alt="" /></p>\r\n<p>#budgetparticipatif</p>\r\n<p>Nous suivre sur <span style="text-decoration: underline;"><span style="color: #0000ff;"><a style="color: #0000ff; text-decoration: underline;" href="https://www.facebook.com/groups/NotreBudget/" target="_blank">Facebook</a></span></span> et sur <span style="text-decoration: underline;"><span style="color: #0000ff;"><a style="color: #0000ff; text-decoration: underline;" href="https://twitter.com/Paris" target="_blank">Twitter</a></span></span></p>\r\n<p style="font-size: 0.875em;">Pour param&eacute;trer les notifications que vous recevez, rendez-vous dans la rubrique &laquo; Mes notifications &raquo; de votre compte sur <span style="text-decoration: underline;"><span style="color: #3366ff; text-decoration: underline;"><a style="color: #3366ff; text-decoration: underline;" href="https://budgetparticipatif.paris.fr/bp/" target="_blank">budgetparticipatif.paris</a></span></span></p>', 'ddct-mpc-site-bp@paris.fr', '', 1, 1),
	(14, 'Ville de Paris - budget participatif', 'budget.participatif@paris.fr', 'À propos de votre projet', '<p>Bonjour,</p>\r\n<p>Merci de votre int&eacute;r&ecirc;t pour le budget participatif.</p>\r\n<p>Le projet intitul&eacute; &laquo; ${idee.titre} &raquo; num&eacute;ro &laquo; ${idee.reference} &raquo; a &eacute;t&eacute; supprim&eacute; en raison de son caract&egrave;re commercial et/ou injurieux. Il n&rsquo;appara&icirc;t plus ni sur le site du budget participatif ni dans la rubrique &laquo; Mon espace &raquo; / &laquo; Mes projets d&eacute;pos&eacute;s &raquo; / &laquo; Mes projets favoris &raquo;.</p>\r\n<p>Nous vous remercions de votre compr&eacute;hension.</p>\r\n<p>Pour toute question, vous pouvez consulter <span style="text-decoration: underline;"><span style="color: #0000ff;"><a style="color: #0000ff; text-decoration: underline;" href="https://budgetparticipatif.paris.fr/bp/jsp/site/Portal.jsp?page=helpdesk&amp;faq_id=3" target="_blank">la foire aux questions du budget participatif</a></span></span> ou nous &eacute;crire &agrave; <span style="text-decoration: underline;"><span style="color: #0000ff;"><a style="color: #0000ff; text-decoration: underline;" href="mailto:budget.participatif@paris.fr" target="_blank">budget.participatif@paris.fr</a></span></span></p>\r\n<p><strong>L\'&eacute;quipe du budget participatif de la Ville de Paris</strong></p>\r\n<p><img src="http://budgetparticipatif.paris.fr/bp/images/local/skin/logo_paris_bp_mini.png" alt="" /></p>\r\n<p>#budgetparticipatif</p>\r\n<p>Nous suivre sur <span style="text-decoration: underline; color: #3366ff;"><a style="color: #3366ff; text-decoration: underline;" href="https://www.facebook.com/groups/NotreBudget/" target="_blank">Facebook</a> </span>et sur <span style="text-decoration: underline;"><span style="color: #0000ff; text-decoration: underline;"><a style="color: #0000ff; text-decoration: underline;" href="https://twitter.com/Paris" target="_blank">Twitter</a></span></span></p>\r\n<p style=